package com.example.appreciclerview;

import android.content.Context;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;


import java.util.ArrayList;
import java.util.List;


public class RestauranteFragment extends Fragment {

    private static final String ARG_COLUMN_COUNT = "column-count";
    private int mColumnCount = 1;
    private RecyclerView recyclerView;
    private List<Restaurante> restauranteList;
    private static OnListFragmentInteractionListener myListener;

    public RestauranteFragment(OnListFragmentInteractionListener listener) {
        this.myListener = listener;
    }

    public static RestauranteFragment newInstance(int columnCount) {
        RestauranteFragment fragment = new RestauranteFragment(myListener);
        Bundle args = new Bundle();
        args.putInt(ARG_COLUMN_COUNT, columnCount);
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        if (getArguments() != null) {
            mColumnCount = getArguments().getInt(ARG_COLUMN_COUNT);
        }
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_item_list, container, false);

        if (view instanceof RecyclerView) {
            Context context = view.getContext();
            recyclerView = (RecyclerView) view;
            if (mColumnCount <= 1) {
                recyclerView.setLayoutManager(new LinearLayoutManager(context));
            } else {
                recyclerView.setLayoutManager(new GridLayoutManager(context, mColumnCount));
            }
            restauranteList=new ArrayList<>();
            restauranteList.add(new Restaurante("Pizza Hut",
                                                "https://d1ralsognjng37.cloudfront.net/3c2c3cf2-143e-42da-9c05-fca34d50cfd3.jpeg",
                                               "Estados Unidos",
                                              4.5f,
                                                    "https://www.pizzahut.com.mx/home"));
            restauranteList.add(new Restaurante("Dominoâ€™s Pizza","data:image/jpeg;base64,","Estados Unidos",4.0f, "https://www.dominos.com.mx/"));
            restauranteList.add(new Restaurante("Telepizza","data:image/jpeg;base64,","EspaÃ±a",5.0f, "https://www.telepizza.es/"));
            restauranteList.add(new Restaurante("Greenwich Pizza","https://media.philstar.com/images/the-philippine-star/lifestyle/on-the-radar/20200206/greenwich-body.jpg","Filipinas",3.5f, "https://www.greenwichpizzeria.com/"));
            restauranteList.add(new Restaurante("PizzaExpress","data:image/jpeg;base64,","Reino Unido",3.0f,"https://web.facebook.com/pizzaexpress/?_rdc=1&_rdr"));
            recyclerView.setAdapter(new MyRestauranteRecyclerViewAdapter(getActivity(), restauranteList, myListener));
        }
        return view;
    }

    @Override
    public void onAttach(@NonNull Context context){
        super.onAttach(context);
        if (context instanceof OnListFragmentInteractionListener){
            myListener= (OnListFragmentInteractionListener) context;
        }else{
            throw  new RuntimeException(context.toString());
        }
    }

    @Override
    public void onDetach() {
        super.onDetach();
        myListener = null;
    }

    public interface OnListFragmentInteractionListener {
        void onListFragmentInteraction(Restaurante restaurante);
    }

}